create table cm_horarios
(
    ID_HORARIO           int auto_increment
        primary key,
    ID_MEDICO            int                                 not null,
    FECHA_ATENCION       timestamp default CURRENT_TIMESTAMP not null on update CURRENT_TIMESTAMP,
    INICIO_ATENCION      time(6)                             not null,
    FINAL_ATENCION       time(6)                             not null,
    ESTADO               enum ('A')                          not null,
    FECHA_REGISTRO       varchar(50) collate utf8mb4_bin     not null,
    USUARIO_REGISTRO     varchar(25)                         not null,
    FECHA_MODIFICACION   varchar(50)                         not null,
    USUARIO_MODIFICACION varchar(25)                         not null
);

